import React from "react";
import { Link } from "react-router-dom";

function Filter({ setActiveFilter }) {
  let All = "All";
  let Done = "Done";
  let Todo = "Todo";

  return (
    <div className="link">
      <Link onClick={e => { setActiveFilter(All) }}to="/ "> {All} </Link> 
       
      <Link onClick={e => setActiveFilter(Done)} to="/done"> {Done} </Link> 
    
      <Link onClick={e => setActiveFilter(Todo)} to="/todo"> {Todo} </Link>
    </div>
  );
}
export default Filter;
